  onGenerateReport(): void {
    const coverElement = document.getElementById('cover-section');
    const contentElement = document.getElementById('content-section-summary');
    const workSummaryContentElement = document.getElementById(
      'content-section-work'
    );

    const pdfWidth = 596; // A4 width in points
    const pdfHeight = 842; // A4 height in points
    const margin = 20; // Margin for PDF content
    const padding = 24; // Padding for page content

    // Helper function to render a single page
    const renderPage = (
      element: HTMLElement | null,
      pdf: jsPDF,
      scaleFactor: number,
      padding: number,
      addPage: boolean
    ): Promise<void> => {
      return new Promise((resolve, reject) => {
        if (!element) return resolve(); // Skip if element is null

        // Check if the element exists
        if (element) {
          // Get all <p> elements within the parent element
          const paragraphs = element.getElementsByTagName('p');

          // Loop through each <p> element and apply the style
          for (let i = 0; i < paragraphs.length; i++) {
            paragraphs[i].style.boxSizing = 'border-box';
            paragraphs[i].style.verticalAlign = 'middle';
            // paragraphs[i].style.paddingBottom = '12px';
            paragraphs[i].style.padding = '12px';
          }
        }

        html2canvas(element, {
          scale: 2, // Higher scale for better quality
          useCORS: true, // Allow cross-origin resources
          allowTaint: true,
        })
          .then(async (canvas) => {
            const imgData = canvas.toDataURL('image/jpeg', 1.0);
            const canvasWidth = canvas.width;
            const canvasHeight = canvas.height;

            // Calculate scaling
            const scaledWidth = pdfWidth - 2 * padding;
            const scaledHeight = (canvasHeight * scaledWidth) / canvasWidth;

            const availableHeight = pdfHeight - 2 * padding;
            let remainingHeight = scaledHeight;
            let yOffset = padding;

            while (remainingHeight > 0) {

              if (addPage) {
                pdf.addPage();
              }

              const heightToRender =
                remainingHeight > availableHeight
                  ? availableHeight
                  : remainingHeight;

              pdf.addImage(
                imgData,
                'JPEG',
                padding,
                yOffset,
                scaledWidth,
                heightToRender
              );

              remainingHeight -= availableHeight;
              yOffset = padding; // Reset offset for the new page
              addPage = true; // Add subsequent pages
            }

            resolve();
          })
          .catch((error) => {
            console.error('Error rendering page:', error);
            reject(error);
          });
      });
    };

    // Function to create the PDF
    const createPDF = async () => {
      const pdf = new jsPDF('p', 'pt', [pdfWidth, pdfHeight]);

      try {
        // Render the cover page
        await renderPage(coverElement, pdf, 1, margin, false);

        // Render the summary content
        await renderPage(contentElement, pdf, 1, margin, true);

        // Render the work summary content
        await renderPage(workSummaryContentElement, pdf, margin, margin, true);

        // Save the PDF
        pdf.save(`Report-${new Date().toISOString()}.pdf`);
      } catch (error) {
        console.error('Error generating PDF:', error);
      }
    };

    createPDF();
  }
